import React, { useState } from "react";
import SearchList from "./SearchList";
import "./SearchPopup.css";

function SearchPopup() {
  const [destina, setdestina] = useState();
  const [minprice, setminprice] = useState();
  //onchange lấy data tuwd destination
  const destinationHandle = (e) => {
    const destina = e.target.value;
    setdestina(e.target.value);
    console.log(destina);
  };
  const minPriceHandle = (e) => {
    const minprice = e.target.value;
    setminprice(e.target.value);
    console.log(minprice);
  };

  return (
    <div className="anh-em">
      <div className="popup">
        <h3>Search</h3>
        <div className=" des">
          <label>Destination</label>
          <br />
          <input
            className="input-des"
            type="text"
            onChange={destinationHandle}
          ></input>
        </div>
        <div className="check-day">
          <label>Check-in Date</label>
          <br />
          <input className="date" type="date"></input>
        </div>
        <h4>Options</h4>
        <div className="mins">
          <span>Min price per night</span>
          <input
            className="input-min"
            type="number"
            onChange={minPriceHandle}
          ></input>

          <span>Max price per night</span>
          <input
            className="input-max"
            type="number"
            // onChange={maxPriceHandle}
          ></input>

          <span>Adult</span>
          <input className="input-adult" type="number"></input>

          <span>Children</span>
          <input className="input-children" type="number"></input>

          <span>Room</span>
          <input className="input-room" type="number"></input>
        </div>
        <button className="search-popup" type="submit">
          Search
        </button>
      </div>

      <div>
        <SearchList />
      </div>
    </div>
  );
}
export default SearchPopup;
