import React from "react";

const hoteldei = [
  {
    name: "Aparthotel Stare Miasto",
    city: "Madrid",
    price: 120,
    rate: 8.9,
    type: "Excellent",
    image_url: "./images/hotel_1.webp",
  },
  {
    name: "Comfort Suites Airport",
    city: "Austin",
    price: 140,
    rate: 9.3,
    type: "Exceptional",
    image_url: "./images/hotel_2.jpg",
  },
  {
    name: "Four Seasons Hotel",
    city: "Lisbon",
    price: 99,
    rate: 8.8,
    type: "Excellent",
    image_url: "./images/hotel_3.jpg",
  },
  {
    name: "Hilton Garden Inn",
    city: "Berlin",
    price: 105,
    rate: 8.9,
    type: "Excellent",
    image_url: "./images/hotel_4.jpg",
  },
];

function HotelDetail() {
  return (
    <div>
      <div className="hotel_detail">
        <img src={hoteldei[0].image_url}></img>
        <h2>{hoteldei[0].city} </h2>
        <p>Starting From ${hoteldei[0].price} </p>
        <p>
          <span>{hoteldei[0].rate}</span> {hoteldei[0].type}
        </p>
      </div>
    </div>
  );
}

export default HotelDetail;
