import React, { useState } from "react";
import "./Header.css";

import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

function Header() {
  //sự kiện chuyerrenr trang của nut search
  const onClickHandler = () => {
    window.location.replace("./search");
  };
  const [datarange, setdatarange] = useState(new Date());
  const [isopen, setOpen] = useState(true);

  const handleSelect = (ranges) => {
    console.log(ranges);
    setdatarange(ranges);
  };

  const selectionRange = {
    startDate: new Date(),
    endDate: new Date(),
    key: "selection",
  };

  return (
    <div className=" header">
      <h4 className="lifetime">A lifetime of discounts? It's Genius.</h4>
      <p>
        Get rewarded for your travels-unlock instant savings of 10% or more with
        a free accout
      </p>
      <button className="sign-in">Sign in/ Register</button>
      <div className="Submit-list">
        <div>
          <i className="fas fa-bed"></i>
          <input type="text" value="where are you going?"></input>
        </div>
        <div>
          <input value={datarange}></input>
          {isopen && (
            <DateRange
              editableDateInputs={true}
              moveRangeOnFirstSelection={false}
              className="date"
              minDate={new Date()}
              onChange={handleSelect}
              ranges={[selectionRange]}
            />
          )}
        </div>
        <div className="list-submit">
          <i class="fa-light fa-person-dress"></i>
          <input className="input" type="number" id="adult" value={1}></input>
          <label for="adult"> adult</label>
          <input
            className="input"
            type="number"
            id="childrent"
            value={0}
          ></input>
          <label for="chilrent"> chilrent</label>
          <input className="input" type="number" id="room" value={1}></input>
          <label for="room"> room</label>
        </div>
        <button
          className="header-search"
          onClick={onClickHandler}
          type="submit"
        >
          Search
        </button>
      </div>
    </div>
  );
}

export default Header;
