import React, { useState, useEffect, useRef } from "react";
import { DateRange } from "react-date-range";
import { addDays, format } from "date-fns";

import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file

const DaterangeComponent = () => {
  const [range, setrange] = useState([
    {
      startDate: new Date(),
      endDate: addDays(new Date(), 7),
      key: "selection",
    },
  ]);
  const [isOpen, setIsopen] = useState(false);

  const refCalen = useRef(null);

  //hanh động ấn bên ngoai
  const anKhiNhanBenNgoai = (event) => {
    console.log(refCalen);
    console.log(event.target);
    if (refCalen.current && !refCalen.current.contains(event.target)) {
      setIsopen(false);
    }
  };
  //ấn esc
  const anKhiNhanEsc = (event) => {
    if (event.key === "Escape") {
      setIsopen(false);
    }
  };

  //sử dụng useEff để load
  useEffect(() => {
    //thêm sự kiện click để load hành độngtrên trang và nhấn esc
    document.addEventListener("click", anKhiNhanBenNgoai, true);
    document.addEventListener("keydown", anKhiNhanEsc, true);
  }, []);

  return (
    <div className="divcontainer">
      <i class="fas fa-calendar-o"></i>
      <input
        value={`${format(range[0].startDate, "dd-MM-yyyy")} to ${format(
          range[0].endDate,
          "dd-MM-yyyy"
        )}`}
        readOnly
        onClick={() => setIsopen(true)}
      ></input>
      <div className="calendar" ref={refCalen}>
        {isOpen && (
          <DateRange
            onChange={(item) => setrange([item.selection])}
            editableDateInputs={true}
            moveRangeOnFirstSelection={false}
            direction="horizontal"
            month={2}
            ranges={range}
          />
        )}
      </div>
    </div>
  );
};

export default DaterangeComponent;
